#! /bin/bash

# Cleanup folder
rm -rf _assets

# Recreate folder
mkdir -p _assets/website/
mkdir -p _assets/ebook/

# Compile Website CSS
./node_modules/.bin/lessc -clean-css src/less/website.less _assets/website/style.css

# Compile eBook CSS
./node_modules/.bin/lessc -clean-css src/less/ebook.less _assets/ebook/ebook.css
./node_modules/.bin/lessc -clean-css src/less/pdf.less _assets/ebook/pdf.css
./node_modules/.bin/lessc -clean-css src/less/mobi.less _assets/ebook/mobi.css
./node_modules/.bin/lessc -clean-css src/less/epub.less _assets/ebook/epub.css
